javascript'> alert('无法删除');</script>";	}//		echo "<script type='text/javascript'> alert('插入成功');</script>";}?><?php$tbl_name="";		//your table name// How many adjacent pages should be shown on each side?$adjacents = 3;/*   First get total number of rows in data table.   If you have a WHERE clause in your query, make sure you mirror it here.*/$query = "SELECT COUNT(*) as num FROM massagist_appt";$total_pages = mysql_fetch_array(mysql_query($query));$total_pages = $total_pages['num'];/* Setup vars for query. */$targetpage = "ad_delete_ord.php"; 	//your file name  (the name of this file)$limit = 7; 								//how many items to show per page$page = $_GET['page'];if($page)	$start = ($page - 1) * $limit; 			//first item to display on this pageelse	$start = 0;								//if no page var is given, set start to 0/* Get data. */$sql = "SELECT * FROM `Order`,MassagistDetail WHERE `Order`.massaid = MassagistDetail.phone AND status IN ('UNCOMMENT','DONE','WARN') LIMIT $start, $limit";$result = mysql_query($sql);/* Setup page vars for display. */if ($page == 0) $page = 1;					//if no page var is given, default to 1.$prev = $page - 1;							//previous page is page - 1$next = $page + 1;							//next page is page + 1$lastpage = ceil($total_pages/$limit);		//lastpage is = total pages / items per page, rounded up.$lpm1 = $lastpage - 1;						//last page minus 1/*	Now we apply our rules and draw the pagination object.	We're actually saving the code to a variable in case we want to draw it more than once.*/$pagination = "";if($lastpage > 1){	$pagination .= "<div class=\"pagination\">";	//previous button	if ($page > 1)		$pagination.= "<a href=\"$targetpage?page=$prev\">上一页</a>";	else		$pagination.= "<span class=\"disabled\">上一页</span>";	//pages	if ($lastpage < 7 + ($adjacents * 2))	//not enough pages to bother breaking it up	{		for ($counter = 1; $counter <= $lastpage; $counter++)		{			if ($counter == $page)				$pagination.= "<span class=\"current\">$counter</span>";			else				$pagination.= "<a href=\"$targetpage?page=$counter\">$counter</a>";		}	}	elseif($lastpage > 5 + ($adjacents * 2))	//enough pages to hide some	{		//close to beginning; only hide later pages		if($page < 1 + ($adjacents * 2))		{			for ($counter = 1; $counter < 4 + ($adjacents * 2); $counter++)			{				if ($counter == $page)					$pagination.= "<span class=\"current\">$counter</span>";				else					$pagination.= "<a href=\"$targetpage?page=$counter\">$counter</a>";			}			$pagination.= "...";			$pagination.= "<a href=\"$targetpage?page=$lpm1\">$lpm1</a>";			$pagination.= "<a href=\"$targetpage?page=$lastpage\">$lastpage</a>";		}		//in middle; hide some front and some back		elseif($lastpage - ($adjacents * 2) > $page && $page > ($adjacents * 2))		{			$pagination.= "<a href=\"$targetpage?page=1\">1</a>";			$pagination.= "<a href=\"$targetpage?page=2\">2</a>";			$pagination.= "...";			for ($counter = $page - $adjacents; $counter <= $page + $adjacents; $counter++)			{				if ($counter == $page)					$pagination.= "<span class=\"current\">$counter</span>";				else					$pagination.= "<a href=\"$targetpage?page=$counter\">$counter</a>";			}			$pagination.= "...";			$pagination.= "<a href=\"$targetpage?page=$lpm1\">$lpm1</a>";			$pagination.= "<a href=\"$targetpage?page=$lastpage\">$lastpage</a>";		}		//close to end; only hide early pages		else		{			$pagination.= "<a href=\"$targetpage?page=1\">1</a>";			$pagination.= "<a href=\"$targetpage?page=2\">2</a>";			$pagination.= "...";			for ($counter = $lastpage - (2 + ($adjacents * 2)); $counter <= $lastpage; $counter++)			{				if ($counter == $page)					$pagination.= "<span class=\"current\">$counter</span>";				else					$pagination.= "<a href=\"$targetpage?page=$counter\">$counter</a>";			}		}	}	//next button	if ($page < $counter - 1)		$pagination.= "<a href=\"$targetpage?page=$next\">下一页 </a>";	else		$pagination.= "<span class=\"disabled\">下一页 </span>";	$pagination.= "</div>\n";}?><div class="formtitle"><span>订单</span></div><table class="tablelist">	<thead>	<tr>		<th>订单号</th>		<th>时间</th>		<th>技师</th>		<th>门店</th>		<th>删除</th>	</tr>	</thead>	<tbody><?phpwhile($row = mysql_fetch_array($result)){	// Your while loop here	?>		<tr class="order-form">			<th class="orderid"> <?php echo $row['orderid']; ?> </th>			<th class="time"> <?php echo $row['time']; ?> </th>			<th class="name"> <?php echo $row['massagistname']; ?> </th>			<th class="shop"> <?php echo $row['shopname']; ?> </th>			<th>				<input type="button" value="删除" class = "delete-btn" onclick="return del();" />			</th>		</tr><?php}?>	</tbody></table><?=$pagination?><?phpif(isset($_POST['is_delete_order'])){	$id=$_POST['id'];	$query="delete from `Order` WHERE orderid = '$id'";	$query_ma="delete from massagist_appt WHERE orderid = '$id'";	mysql_query($query,$con);	mysql_query($query_ma,$con);}?>