<!DOCTYPE html><html lang="en"><head>	<meta charset="UTF-8">	<title>删除推荐新闻</title></head><?phprequire_once "db_func.php";$con = DBconnect();$select_rcmand_ma = "SELECT * FROM Recommand_news ";?><center>	<body>	<form id="delete_rcmand_news" action="?delete_rcmand_news" method="post">		<select id="delete_rcmand_news" name="delete_rcmand_news">			<?php			$massa_name = array();			$rs = mysql_query($select_rcmand_ma);			$nr = mysql_num_rows($rs);			echo "<option disabled selected> -- select a news -- </option>";			for ($i=0; $i<$nr; $i++){				$r = mysql_fetch_array($rs);				echo "<option>".$r["title"]."</option>";			}			?>		</select>		</br>		<input type="submit"/>		<input type="reset"/>	</form>	</body></center><?phpif (isset($_POST['delete_rcmand_news'])) {	$name = $_POST['delete_rcmand_news'];	$sql = "DELETE FROM Recommand_news WHERE name= '$name'" ;	if (mysql_query($sql,$con)) {		echo "<script type='text/javascript'> alert('删除成功');</script>";	} else {		echo "<script type='text/javascript'> alert('无法删除，请检查输入信息');</script>";	}}?>