<?phpif (!session_id()){	session_start();}?><!DOCTYPE html><html lang="en" xmlns="http://www.w3.org/1999/html"><head>	<meta charset="UTF-8">	<title>订单查询结果</title><?php	require_once "db_func.php";	$con = DBconnect();	$start_time = array();	$_SESSION['start'] = null;?><?php	$selected_year_month = $_POST["year"] ."-".$_POST["month"];	$_SESSION['start'] = $selected_year_month;	$selected_year = $_POST["year"];	$selected_month = $_POST["month"];	order_detail($con,$selected_year,$selected_month);	function order_detail($con,$selected_year,$selected_month){		$sql_start_time = "SELECT * FROM massagist_appt WHERE YEAR(start)='$selected_year' AND MONTH(start)='$selected_month'";		$result = mysql_query($sql_start_time, $con ) or die( "Fetch massaid Error:" . mysql_error());		echo "<center>";		echo "<TR>订单详情</TR>";		echo "<table border='1'>";		echo "<TR><TD>ORDERID</TD><TD>MASSAID</TD><TD>SERVICEID</TD><TD>CUSTOMERID</TD><TD>STATUS</TD></TR>";		while ($row = mysql_fetch_assoc($result)){			echo "<tr><td>";			echo $row['orderid'];			echo "</td><td>";			echo $row['massaid'];			echo "</td><td>";			echo $row['serviceid'];			echo "</td><td>";			echo $row['customerid'];			echo "</td><td>";			echo get_order_status($con,$row['orderid']);			echo "</td></tr>";		}		echo "</table>";		echo "</center>";	}	function get_order_status($con,$orderid){		$sql_order_status = "SELECT status FROM `Order` WHERE orderid = '$orderid'";		$result = mysql_query($sql_order_status,$con) or die("error".mysql_errno());		$row = mysql_fetch_assoc( $result );		return $row['status'];	}