"> <?php echo $row['service_start']; ?></th>			<th class="service"> <?php echo  get_service_name($con,$row['serviceid']); ?></th>			<th class="customer"> <?php echo $row['customerid']; ?></th>			<th class="comment"> <?php				if(get_comment($con,$order)){					echo get_comment($con,$order);				}				else{					echo "暂无评价";				}				?>			</th>		</tr>		<?php	}	?>	</tbody></table></body></html><?phpfunction get_service_name($con,$sid){	$name="select name from Service where shopid=$sid";	$result = mysql_query( $name, $con );	$row = mysql_fetch_assoc($result);	return $row['name'];}function get_comment($con,$orderid){	$name="select content from Comment where orderid=$orderid";	$result = mysql_query( $name, $con );	$row = mysql_fetch_assoc($result);	return $row['name'];}function get_service_start($con,$orderid){	$start="select service_start from Comment,massagist_appt where Comment.orderid=massagist_appt.orderid and orderid=$orderid";	$result=mysql_query($start,$con);	$row=mysql_fetch_assoc($result);	return $row['service_start'];}