<?phprequire_once"../db_func.php";$con=DBconnect();if(isset($_POST['is_delete_user'])){	$id=$_POST['id'];	$query="delete from Service WHERE serviceid = '$id'";	mysql_query($query,$con);}if(isset($_POST['is_edit_user'])){	$old_id=$_POST['old_id'];	$service_name=$_POST['service_name'];	$price=$_POST['price'];	$p_high=$_POST['p_high'];	$p_expert=$_POST['p_expert'];	$duration=$_POST['duration'];	$update_service="update Service set name = '$service_name', price ='$price',price_high='$p_high', price_expert='$p_high' , duration='$duration' WHERE serviceid = '$old_id'";	mysql_query($update_service,$con);}echo json_encode($_POST,true);