<html><script language="javascript">	function validateForm() {		var = confirm("Want to delete?");		var x = document.getElementById('test').value;		if (x == null || x == 0 || x == "0") {			alert("Stop");		}		else			document.form.submit();	}</script><body><form id="f" method="post" name="form" onsubmit="return validateForm();" action="#">	<input type="text" name="fname" id="test" />	<input type="button" value="Submit" onclick='validateForm()'/></form></body></html>